import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.css';
import './Demo.scss';
import { useParams } from 'react-router-dom';
import Particles from '../elements/Particles';

function Demo() {
    const [messages, setMessages] = useState([]); // State to store the messages
    const [inputValue, setInputValue] = useState(''); // State to track textarea input

    // Function to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();

        if (inputValue.trim() !== '') {
        // Append new message on top of the list
        setMessages([inputValue, ...messages]);
        setInputValue(''); // Clear textarea after submit
        }
    };

    return (
        <div className='topContainer'>
            <div className="characterDisplay">
                <div className="characterContainer">
                {/* Form for submitting messages */}
                <form onSubmit={handleSubmit}>
                    <textarea
                    className="form-control"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Enter your message"
                    rows="3"
                    />
                    <button type="submit" className="btn btn-primary mt-2">
                    Submit Message
                    </button>
                </form>

                {/* Display the submitted messages */}
                <div className="messageList mt-3">
                    {messages.length > 0 ? (
                    messages.map((message, index) => (
                        <div key={index} className="messageItem alert alert-secondary">
                        {message}
                        </div>
                    ))
                    ) : (
                    <p>No messages yet.</p>
                    )}
                </div>
                </div>
            </div>
            <Particles />
        </div>
    );
}

export default Demo;
