import React, { useState, useEffect, useRef } from "react";
import './Demo.scss';
import Particles from '../elements/Particles';

function Demo() {
    const [messages, setMessages] = useState([]); // State to store the messages
    const [inputValue, setInputValue] = useState(''); // State to track textarea input
    const messagesEndRef = useRef(null); // Ref to scroll to the latest message

    // Function to auto-scroll to the bottom
    const scrollToBottom = () => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    };

    useEffect(() => {
        scrollToBottom(); // Scroll to the latest message when messages change
    }, [messages]);

    // Function to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        if (inputValue.trim() !== '') {
            // Append new message on top of the list
            setMessages([inputValue, ...messages]);
            setInputValue(''); // Clear textarea after submit
        }
    };

    // Function to auto-resize the textarea based on content
    const handleInputChange = (e) => {
        const textareaLineHeight = 24; // Approx line height of textarea
        e.target.style.height = 'auto'; // Reset the height
        e.target.style.height = `${e.target.scrollHeight}px`; // Adjust to fit content

        setInputValue(e.target.value); // Update input state
    };

    return (
        <div className='topContainer'>
            <div className="chatDisplay">
                <div className="chatContainer">
                    {/* Form for submitting messages */}
                    <form onSubmit={handleSubmit} className="chatForm">
                        <textarea
                            className="chatInput"
                            value={inputValue}
                            onChange={handleInputChange}
                            placeholder="Enter your message"
                            rows="1" /* Initially 1 line */
                            style={{ overflowY: 'auto' }} /* Ensure smooth scrolling */
                        />
                        <button type="submit" className="submitButton">
                            Submit
                        </button>
                    </form>

                    {/* Display the submitted messages */}
                    <div className="messageList">
                        {messages.length > 0 ? (
                            messages.map((message, index) => (
                                <div key={index} className="messageItem">
                                    {message}
                                </div>
                            ))
                        ) : (
                            <p>No messages yet.</p>
                        )}
                        <div ref={messagesEndRef} /> {/* Scroll target */}
                    </div>
                </div>
            </div>
            {/* Ensure Particles have a lower z-index so they don't block elements */}
            <Particles style={{ zIndex: 0 }} />
        </div>
    );
}

export default Demo;
