import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.css';
import './Demo.scss';
import { useParams } from 'react-router-dom';
import Particles from '../elements/Particles';

function Demo() {
    const [message, setMessage] = useState(""); // State for the current message input
    const [messages, setMessages] = useState([]); // State for storing all submitted messages

    // Function to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim()) {
            // Add the new message to the top of the messages array
            setMessages([message, ...messages]);
            // Clear the message input
            setMessage("");
        }
    };

    return (
        <div className='topContainer'>
            <div className="characterDisplay">
                <div className="characterContainer">
                    {/* Display messages */}
                    {messages.map((msg, index) => (
                        <div key={index} className="messageItem">
                            {msg}
                        </div>
                    ))}
                </div>

                {/* Form to submit a new message */}
                <form onSubmit={handleSubmit}>
                    <textarea
                        className="form-control"
                        rows="3"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)} // Update the message state
                        placeholder="Enter your message"
                    />
                    <button type="submit" className="btn btn-primary mt-2">
                        Submit
                    </button>
                </form>
            </div>
            <Particles />
        </div>
    );
}

export default CharacterPage;
